mapper.select:
SELECT *
  FROM (
  		SELECT sub.*, ROWNUM AS RNUM
          FROM ( 
          		select *
                   from board 
                   where kind_no = #{kindNo}
                   order by board_no desc
                ) sub
       )
 WHERE RNUM BETWEEN #{FROM} AND #{TO}

select count(*)
from board
where kind_no = #{kindNo}
